INSERT INTO `t_card_adjust` VALUES ('1027', '1128', '[执念之剑]<br> &lt;執念の剣&gt;<br> [2010/09/08]<br> <br> ●装备怪兽的攻击力·守备力上升500分。<br> ◇不进入连锁<br> ●这张卡送去墓地时，这张卡回到卡组的最上方。<br> ◇进入连锁（1速）<br> ◇必须发动<br> ◇效果处理时这张卡不在墓地存在的场合，这个效果不适用', '2', '2020-05-22 11:24:35', '2020-05-22 11:24:35');
